import React, { useState } from 'react';

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleOptionClick = (rowIndex, index) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[rowIndex] = index;
      return updatedOptions;
    });
  };

  const checkAnswers = () => {
    let score = 0;
    jsonData.forEach((questionData, rowIndex) => {
      const answer = questionData.answer[0];
      const selectedOption = selectedOptions[rowIndex];
      if (selectedOption === answer) {
        score++;
      }
    });
    console.log(`Score: ${score}`);
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => (
          <div key={index}>
            <h1>Type: {questionData.type}</h1>
            <h1>Question: {questionData.question}</h1>
            <OptionButtons
              options={questionData.options}
              answer={questionData.answer[0]}
              rowIndex={index}
              selectedOption={selectedOptions[index]}
              handleOptionClick={handleOptionClick}
            />
          </div>
        ))}
      </div>
      <button className="submit-button" onClick={checkAnswers}><strong>Submit</strong></button>
    </div>
  );
};

export default OptionButtons;
