import React, { useState, useEffect, useRef } from 'react';

const Clock = () => {
  const [time, setTime] = useState(new Date());
  const clockElement = useRef(null);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(new Date());
    }, 1000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  useEffect(() => {
    if (clockElement.current) {
      clockElement.current.innerText = time.toLocaleTimeString();
    }
  }, [time]);

  return (
    <div className="Clock">
      <h1>Current Time:</h1>
      <h2 ref={clockElement}></h2>
    </div>
  );
};

export default Clock;
