import React, { useState } from 'react';

const WhatsNext = ({ options, answer, onAnswerSubmit }) => {
    const [userAnswer, setUserAnswer] = useState('');

  const handleInputChange = (e) => {
    setUserAnswer(e.target.value);
  };

  const checkAnswer = () => {
    const parsedAnswer = parseInt(userAnswer);
    if (parsedAnswer === answer) {
      // Correct answer, award a point
      console.log('Correct answer! Awarding a point.');
    } else {
      // Incorrect answer
      console.log('Incorrect answer! No point awarded.');
    }
    // You can perform any other actions related to checking the answer here
  };

  return (
    <div className="whats-next-container">
      <div className="options-container">
        {options.map((option, index) => (
          <div key={index} className="option-box">
            {option}
          </div>
        ))}
      </div>
      <input
        type="text"
        placeholder="Your answer"
        value={userAnswer}
        onChange={handleInputChange}
      />
      <button className="submit-button" onClick={checkAnswer}>
        Submit
      </button>
    </div>
  );
};

export default WhatsNext;
