import React, { useState } from 'react';

const WhatsNext = ({ options, answer, index, onSubmit }) => {
  const [selectedOptions, setSelectedOptions] = useState(Array(options.length).fill(null));
  const [userAnswer, setUserAnswer] = useState('');

  const handleOptionClick = (rowIndex, optionIndex) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[rowIndex] = optionIndex;
      return updatedOptions;
    });
  };

  const handleAnswerChange = (event) => {
    setUserAnswer(event.target.value);
  };

  const checkAnswer = () => {
    onSubmit(index, { options: selectedOptions, answer: userAnswer });
  };

  return (
    <div className="whats-next-container">
      <div className="row-container">
        {options.map((rowOptions, rowIndex) => (
          <div key={rowIndex} className="row">
            {rowOptions.map((option, optionIndex) => (
              <div
                key={optionIndex}
                className={`whats-next-box ${selectedOptions[rowIndex] === optionIndex ? 'selected' : ''}`}
                onClick={() => handleOptionClick(rowIndex, optionIndex)}
              >
                {option}
              </div>
            ))}
          </div>
        ))}
      </div>
      <input
        type="text"
        className="answer-input"
        placeholder="Enter your answer"
        value={userAnswer}
        onChange={handleAnswerChange}
      />
      <button className="submit-button" onClick={checkAnswer}><strong>Submit</strong></button>
    </div>
  );
};

export default WhatsNext;
