import React, { useState } from 'react';

const OptionButton = ({ option, onClick }) => {
  const [active, setActive] = useState(false);

  const handleClick = () => {
    setActive(true);
    onClick(option);
  };

  return (
    <button
      className={`option-box ${active ? 'selected' : ''}`}
      style={{ backgroundColor: active ? 'red' : 'white' }}
      onClick={handleClick}
    >
      {option}
    </button>
  );
};

export default OptionButton;
