import React, { useState } from 'react';

const WhatsNext = ({ options, answer, index, onSubmit }) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  const checkAnswer = () => {
    onSubmit(index, selectedOption);
  };

  return (
    <div className="whats-next-container">
      {options.map((option, optionIndex) => (
        <button
          key={optionIndex}
          className={`whats-next-button ${selectedOption === option ? 'selected' : ''}`}
          onClick={() => handleOptionClick(option)}
        >
          {option}
        </button>
      ))}
      <button className="submit-button" onClick={checkAnswer}><strong>Submit</strong></button>
    </div>
  );
};

export default WhatsNext;
