import React, { useState } from 'react';
import Clock from './Clock';
import jsonData from './testinfo.json';
import OptionButtons from './OptionButtons';
import SubmitButton from './SubmitButton';

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleOptionSelection = (questionIndex, selectedOption) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[questionIndex] = selectedOption;
      return updatedOptions;
    });
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => (
          <div key={index}>
            <h1>Type: {questionData.type}</h1>
            <h1>Question: {questionData.question}</h1>
            <OptionButtons
              options={questionData.options}
              answer={questionData.answer[0]}
              questionIndex={index}
              selectedOption={selectedOptions[index]}
              handleOptionSelection={handleOptionSelection}
            />
          </div>
        ))}
      </div>
      <SubmitButton selectedOptions={selectedOptions} jsonData={jsonData} />
    </div>
  );
};

export default App;
