import React from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';
import SingleButtons from './SingleButtons';
import WhatsNext from './WhatsNext';

const App = () => {
  const [userAnswers, setUserAnswers] = useState([]);

  const handleAnswerSelect = (answer) => {
    setUserAnswers((prevUserAnswers) => [...prevUserAnswers, answer]);
  };

  const handleWhatsNextAnswerSubmit = (answer) => {
    setUserAnswers((prevUserAnswers) => [...prevUserAnswers, answer]);
  };

  const handleSubmission = () => {
    // Create a data object to store the question and answers
    const data = jsonData.map((questionData, index) => ({
      Question: questionData.question,
      Answer: userAnswers[index] || null,
    }));

    // Convert the data object to JSON
    const jsonDataString = JSON.stringify(data);

    // Create a Blob object with the JSON data
    const blob = new Blob([jsonDataString], { type: 'application/json' });

    // Create a download link
    const downloadLink = document.createElement('a');
    downloadLink.href = URL.createObjectURL(blob);
    downloadLink.download = 'user_answers.json';

    // Trigger the download
    downloadLink.click();
  };

  return (
    <div className="app">
      {/* ... */}

      <div className="question-container">
        {jsonData.map((questionData, index) => {
          if (questionData.type === 'single') {
            return (
              <div key={index}>
                {/* ... */}
                <SingleButtons
                  options={questionData.options}
                  answer={questionData.answers}
                  onAnswerSelect={handleAnswerSelect}
                />
              </div>
            );
          } else if (questionData.type === 'whatsNext') {
            return (
              <div key={index}>
                {/* ... */}
                <WhatsNext
                  options={questionData.options}
                  answer={questionData.answers[0]}
                  onAnswerSubmit={handleWhatsNextAnswerSubmit}
                />
              </div>
            );
          }
          return null;
        })}
      </div>

      <button className="submit-button" onClick={handleSubmission}>
        <strong>Submit</strong>
      </button>
    </div>
  );
};
