import React, { useState } from 'react';

const OptionButton = ({ option, index }) => {
  const [active, setActive] = useState(false);

  const handleClick = () => {
    setActive(!active);
  };

  return (
    <button
      onClick={handleClick}
      style={{ backgroundColor: active ? 'red' : 'white' }}
    >
      {option}
    </button>
  );
};

export default OptionButton;
