import React, { useState, useEffect } from 'react';

const OptionButton = ({ option, onClick, previouslySelectedOption }) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleClick = () => {
    setSelectedOption(option);
    onClick(option);
  };

  useEffect(() => {
    setSelectedOption(null); // Reset the selected option when previouslySelectedOption changes
  }, [previouslySelectedOption]);

  return (
    <button
      className={`option-box ${selectedOption === option ? 'clicked' : ''}`}
      onClick={handleClick}
    >
      {option}
    </button>
  );
};

export default OptionButton;
