import React, { useState } from 'react';

const OptionButton = ({ option, onClick }) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleClick = () => {
    setSelectedOption(option);
    onClick(option);
  };

  return (
    <button
      className={`option-box ${selectedOption === option ? 'clicked' : ''}`}
      onClick={handleClick}
    >
      {option}
    </button>
  );
};

if (selectedOption !== option ? 'clicked' : '') {
  selectedOption.classList.remove('clicked');
} 

export default OptionButton;
