import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';
import SingleButtons from './SingleButtons';

const App = () => {
  const [userAnswers, setUserAnswers] = useState([]);

  const handleAnswerSubmit = () => {
    const userAnswersData = jsonData.map((questionData, index) => {
      return {
        id: questionData.id,
        question: questionData.question,
        userAnswer: userAnswers[index]
      };
    });

    const jsonDataString = JSON.stringify(userAnswersData, null, 2);

    // Code to transfer jsonDataString to UserAnswers.json on the server or perform any other necessary action

    console.log('User Answers Submitted:', userAnswersData);
  };

  const handleSingleButtonSubmit = (index, answer) => {
    setUserAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[index] = answer;
      return updatedAnswers;
    });
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => {
          if (questionData.type === 'single') {
            return (
              <div key={index}>
                <h1>Type: {questionData.type}</h1>
                <h1>Question: {questionData.question}</h1>
                <SingleButtons
                  options={questionData.options}
                  answer={questionData.answers}
                  index={index}
                  onSubmit={handleSingleButtonSubmit}
                />
              </div>
            );
          } else if (questionData.type === 'whatsNext') {
            return (
              <div key={index}>
                <h1>Type: {questionData.type}</h1>
                <h1>Question: {questionData.question}</h1>
                {/* <WhatsNext options={questionData.options} answer={questionData.answers} /> */}
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
      <button className="submit-button" onClick={handleAnswerSubmit}><strong>Submit Answers</strong></button>
    </div>
  );
};

export default App;
