import React, { useState } from 'react';

const OptionButtons = ({ options, answer, questionIndex, selectedOption, handleOptionSelection }) => {
  return (
    <div className="options-container">
      {options.map((option, index) => (
        <div key={index} className="option-box-container">
          <button
            className={`option-box ${selectedOption === option ? 'selected' : ''}`}
            onClick={() => handleOptionSelection(questionIndex, option)}
          >
            {option}
          </button>
        </div>
      ))}
    </div>
  );
};

export default OptionButtons;
