import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';
import OptionButtons from './OptionButtons';

const App = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [points, setPoints] = useState(0);

  const handleOptionSelect = (option, index) => {
    setSelectedOption(index);
  };

  const handleSubmit = () => {
    if (selectedOption === jsonData.answer) {
      setPoints(points + 1);
    }
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        <h1>Type: {jsonData.type}</h1>
        <h1>Question: {jsonData.question}</h1>
        <OptionButtons
          options={jsonData.options}
          onSelect={handleOptionSelect}
        />
      </div>
      <h1>Answer: {jsonData.answer}</h1>
      <button className="submit-button" onClick={handleSubmit}><strong>Submit</strong></button>
      {selectedOption !== null && <h1>Points: {points}</h1>}
    </div>
  );
};

export default App;
