import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';
import OptionButton from './OptionButtons';

const App = () => {
  const [selectedOption, setSelectedOption] = useState(null);
  const [previouslySelectedOption, setPreviouslySelectedOption] = useState(null);

  // Define your function for handling the option button clicks
  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setPreviouslySelectedOption(selectedOption);
    console.log('Option clicked:', option);
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        <h1>Type: {jsonData.type}</h1>
        <h1>Question: {jsonData.question}</h1>
        <div className="options-container">
          {jsonData.options.map((option, index) => (
            <OptionButton
              key={index}
              option={option}
              onClick={handleOptionClick}
              previouslySelectedOption={previouslySelectedOption}
            />
          ))}
        </div>
        <h1>Answer: {jsonData.answer}</h1>
      </div>
    </div>
  );
};

export default App;
