import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';
import OptionButtons from './OptionButtons';

const App = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [score, setScore] = useState(0);

  const handleOptionClick = (option, rowIndex, index) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[rowIndex] = index;
      return updatedOptions;
    });
  };

  const checkAnswers = () => {
    let totalScore = 0;
    jsonData.forEach((questionData, index) => {
      const answer = questionData.answers;
      const selectedOption = selectedOptions[index];
      if (selectedOption === answer) {
        totalScore++;
      }
    });
    setScore(totalScore);
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => {
          if (questionData.type === 'single') {
            return (
              <div key={index}>
                <h1>Type: {questionData.type}</h1>
                <h1>Question: {questionData.question}</h1>
                <OptionButtons
                  options={questionData.options}
                  selectedOptions={selectedOptions}
                  handleOptionClick={handleOptionClick}
                  rowIndex={index}
                />
              </div>
            );
          }
        })}
      </div>
      <button className="submit-button" onClick={checkAnswers}>
        <strong>Submit</strong>
      </button>
      <p>Score: {score}</p>
    </div>
  );
};

export default App;
