import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';

const App = () => {
  const [userAnswers, setUserAnswers] = useState([]);

  const handleAnswerSubmit = (index, answer) => {
    setUserAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[index] = answer;
      return updatedAnswers;
    });
  };

  const submitAnswers = () => {
    if (userAnswers.length < jsonData.length) {
      console.log('Please answer all questions before submitting.');
      return;
    }

    const userAnswersData = jsonData.map((questionData, index) => {
      return {
        id: questionData.id,
        question: questionData.question,
        userAnswer: userAnswers[index],
      };
    });

    // Perform further processing or save the userAnswersData as needed
    console.log(userAnswersData);
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => {
          if (questionData.type === 'single') {
            return (
              <div key={index}>
                <h1>Type: {questionData.type}</h1>
                <h1>Question: {questionData.question}</h1>
                <SingleButtons
                  options={questionData.options}
                  answer={questionData.answers}
                  index={index}
                  onSubmit={handleAnswerSubmit}
                />
              </div>
            );
          } else if (questionData.type === 'whatsNext') {
            return (
              <div key={index}>
                <h1>Type: {questionData.type}</h1>
                <h1>Question: {questionData.question}</h1>
                {/* <WhatsNext options={questionData.options} answer={questionData.answers} /> */}
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
      <button className="submit-button" onClick={submitAnswers}><strong>Submit</strong></button>
    </div>
  );
};

export default App;
