import React, { useState } from 'react';

const WhatsNext = ({ options, answer, index, onSubmit }) => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleOptionClick = (option, rowIndex, optionIndex) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[rowIndex] = optionIndex;
      return updatedOptions;
    });
  };

  const checkAnswers = () => {
    selectedOptions.forEach((selectedOption, rowIndex) => {});
    console.log(selectedOptions);
    onSubmit(index, selectedOptions);
  };

  return (
    <div className="whats-next-container">
      {options.map((rowOptions, rowIndex) => (
        <div key={rowIndex} className="row-container">
          {Array.isArray(rowOptions) ? (
            <>
              {rowOptions.map((option, optionIndex) => (
                <div
                  key={optionIndex}
                  className={`whats-next-box ${selectedOptions[rowIndex] === optionIndex}`}
                  onClick={() => handleOptionClick(option, rowIndex, optionIndex)}
                >
                  {option}
                </div>
              ))}
              <input type="text" className="answer-input" />
            </>
          ) : (
            <p>Error: Invalid options for row {rowIndex}</p>
          )}
        </div>
      ))}
      <button className="submit-button" onClick={checkAnswers}><strong>Submit</strong></button>
    </div>
  );
};

export default WhatsNext;
