import React, { useState } from 'react';

const OptionButton = ({ option, onClick }) => {
  const [lastPressedOption, setLastPressedOption] = useState(null);

  const handleButtonClick = () => {
    setLastPressedOption(option);
    onClick(option);
  };

  return (
    <button
      className={`option-button ${option === lastPressedOption ? 'red' : ''}`}
      onClick={handleButtonClick}
    >
      {option}
    </button>
  );
};

export default OptionButton;
