import React, { useState } from 'react';
import './App.css';
import Clock from './Clock';
import jsonData from './testinfo.json';


const App = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [submitClicked, setSubmitClicked] = useState(false);

  const handleOptionSelection = (questionIndex, option) => {
    setSelectedOptions((prevSelectedOptions) => {
      const updatedOptions = [...prevSelectedOptions];
      updatedOptions[questionIndex] = option;
      return updatedOptions;
    });
  };

  const checkAnswers = () => {
    let score = 0;
    jsonData.forEach((questionData, questionIndex) => {
      const selectedOption = selectedOptions[questionIndex];
      const correctAnswer = questionData.answer[0]; // Assuming there is only one correct answer for each question
      if (selectedOption === correctAnswer) {
        score++;
      }
    });
    console.log(`Score: ${score}`);
    setSubmitClicked(true); // Disable the submit button
  };

  return (
    <div className="app">
      <h1>Test</h1>
      <Clock />
      <div className="question-container">
        {jsonData.map((questionData, index) => (
          <div key={index}>
            <h1>Type: {questionData.type}</h1>
            <h1>Question: {questionData.question}</h1>
            <OptionButtons
              options={questionData.options}
              answer={questionData.answer}
              questionIndex={index}
              selectedOption={selectedOptions[index]}
              handleOptionSelection={handleOptionSelection}
              submitClicked={submitClicked}
            />
          </div>
        ))}
      </div>
      <button className="submit-button" onClick={checkAnswers} disabled={submitClicked}>
        <strong>Submit</strong>
      </button>
    </div>
  );
};

export default App;
